// redirect download link :
const redirectLink = (id) => {
  window.location.href = `/admin/archive.php?download&archive=${id}`;
};

// delete event handler:
const deleteEventHandler = (e) => {
  const row = $(e).parentsUntil("tr").parent();
  const id = row.children('[data-field="id"]').attr("aria-label");
  // ajax //
  $.ajax({
    url: "/",
    method: "POST",
    data: {
      delete_archive: "submit",
      id: id,
    },
    success: (response) => {
      if (response.status === "success") {
        $("#kt_datatable").KTDatatable().reload();
      }
      message(response.status, response.info);
    },
    error: (xhr) => {},
  });
};

jQuery(document).ready(function () {
  KTDatatableDataLocalDemo.init();
});
